class Solution:
    def goodDaysToRobBank(self, s: List[int], time: int) -> List[int]:
        @cache
        def count_left(cur):
            if cur == 0:
                return 0

            if s[cur-1] >= s[cur]:
                return 1 + count_left(cur-1)

            return 0

        @cache
        def count_right(cur):
            if cur == len(s) - 1:
                return 0

            if s[cur+1] >= s[cur]:
                return 1 + count_right(cur+1)

            return 0

        # precaching data
        count_left(0) # O(n)
        count_right(len(s)-1) # O(n)

        ans = []
        for i in range(len(s)):
            left = count_left(i) # O(1)
            right = count_right(i) # O(1)
            if left >= time and right >= time:
                ans.append(i)

        return ans
```

