class Solution:
    def goodDaysToRobBank(self, security: List[int], time: int) -> List[int]:
        n = len(security)
        right = [False] * n
        left = [False] * n
        res = []

        cur_False = [True] * n
        total_False = 0
        for i in range(1,n):
            if security[i] > security[i-1]:
                if i <= time:
                    total_False += 1
                cur_False[i] = False

        for i in range(time,n):
            if total_False == 0:left[i] = True

            if i == n-1:continue
            if cur_False[i-(time-1)] == False:total_False -= 1
            if cur_False[i+1] == False: total_False += 1

        cur_False = [True] * n
        total_False = 0
        for i in range(n-2,-1,-1):
            if security[i] > security[i+1]:
                if n-1-time <= i:total_False += 1
                cur_False[i] = False

        for i in range(n-1-time,-1,-1):
            if total_False == 0:right[i] = True

            if i == 1:continue
            if cur_False[i+(time-1)] == False:total_False -= 1
            if cur_False[i-1] == False: total_False += 1

        for i in range(n):
            if left[i] and right[i]:
                res.append(i)

        return res
