class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& a, int time) {
        int n=a.size();
        vector<int>v;
        int cnt=0;
        vector<int>pref(n,1),suff(n,1);
        for(int i=0;i<n;i++){
            if(i==0)continue;
            if(a[i]<=a[i-1]){
                cnt++;
                pref[i]+=cnt;
            }else{
                cnt=0;
            }
        }
        cnt=0;
        for(int i=n-1;i>=0;i--){
            if(i==n-1)continue;
            if(a[i]<=a[i+1]){
                cnt++;
                suff[i]+=cnt;
            }else{
                cnt=0;
            }
        }
        for(int i=0;i<n;i++){
            if(pref[i]>=time+1&&suff[i]>=time+1){
                v.push_back(i);
            }
        }

        return v;
    }
};
```

