use std::collections::HashSet;
use std::iter::FromIterator;
 impl Solution {
    pub fn good_days_to_rob_bank(security: Vec<i32>, time: i32) -> Vec<i32> {
        let mut pl = i32::MIN;
        let mut pr = i32::MIN;

        let mut cl = 0;
        let mut cr = 0;

        let mut a: HashSet<i32> = HashSet::new();
        let mut b: HashSet<i32> = HashSet::new();
        for i in 0..security.len() {
            if security[i] <= pl {
                cl += 1;
            } else {
                cl = 0;
            }
            if security[security.len() - 1 - i] <= pr {
                cr += 1;
            } else {
                cr = 0;
            }

            if cl >= time {
                a.insert(i as i32);
            }
            if cr >= time {
                b.insert((security.len() - 1 - i) as i32);
            }
            pl = security[i];
            pr = security[security.len() - 1 - i];
        }
        let x = Vec::from_iter(HashSet::from(&a & &b).iter().cloned());
        x
    }
}
```

