func goodDaysToRobBank(security []int, time int) []int {
    var ans []int
    var n int = len(security)
    right := make([]int, n)
    left := make([]int, n)
    for i := n - 2; i >= 0; i-- {
        if security[i] <= security[i + 1] {
            right[i] = right[i + 1] + 1
        }
    }
    for i := 1; i < n; i++ {
        if security[i] <= security[i - 1] {
            left[i] = left[i - 1] + 1
        }
