    def goodDaysToRobBank(self, security: List[int], time: int) -> List[int]:

        # O(n) time and space

        n = len(security)
        # number of non-increasing days before i-th day
        before = [0] * n
        # number of non-decreasing days after i-th day
        after = [0] * n

        count_before, count_after = 0, 0

        # fill arrays before and after in one pass
        for i in range(1, n):
            if security[i - 1] >= security[i]:
                count_before += 1
                before[i] = count_before
            else:
