            if security[n - i - 1] <= security[n - i]:
                count_after += 1
                after[n - i - 1] = count_after
            else:
                count_after = 0

        result = []

        # the i-th day is considered good if before[i] and after[i] are greater than time
        for i in range(n):
            if before[i] >= time and after[i] >= time:
                result.append(i)

        return result

