            Arrays.fill(vis,false);
            max = Math.max(ans,max);
        }

        return max;

    }

    private int DFS(int i,List<List<Integer>> adj,boolean []vis){
        vis[i]=true;
        int ans =1;
        for(int nd : adj.get(i)){
            if(!vis[nd])
                ans+=DFS(nd,adj,vis);
        }

        return ans;
    }
}
```

