class Solution {
public:
    // Helper function to calculate distance between two points
    int distance(int x, int y, int x1, int y1)
    {
        double temp = sqrt(pow(x1 - x, 2) + pow(y1 - y, 2));  // Euclidean distance formula
        return ceil(temp);  // Round up the distance to the nearest integer
    }

    // Depth-first search to calculate the number of bombs that can be detonated starting from a given index
    int dfs(int index, vector<int>& visited, vector<vector<int>>& graph)
    {
        int temp = 1;  // Initialize the count to 1, considering the current bomb itself as detonated
        visited[index] = 1;  // Mark the current index as visited

        // Iterate through the adjacent bombs in the graph
        for (auto x : graph[index])
        {
            if (visited[x] == 0)  // If the adjacent bomb has not been visited
            {
                temp += dfs(x, visited, graph);  // Recursively calculate the count of detonated bombs
            }
        }

        return temp;  // Return the total count of detonated bombs
    }

    int maximumDetonation(vector<vector<int>>& bombs) {
        int n = bombs.size();  // Total number of bombs

        // Create a graph to represent the relationships between bombs
        vector<vector<int>> graph(n);

        // Construct the graph by checking the distances between each pair of bombs
        for (int i = 0; i < n; i++)
        {
            for (int j = i + 1; j < n; j++)
            {
                int x = bombs[i][0];
                int y = bombs[i][1];
                int x1 = bombs[j][0];
                int y1 = bombs[j][1];
                int r = bombs[i][2];
                int r1 = bombs[j][2];

                // Calculate the distance between the bombs and check if they can detonate each other
                int temp = distance(x, y, x1, y1);
                if (temp <= r)
                {
                    graph[i].push_back(j);  // Add j as an adjacent bomb to i
                }
                if (temp <= r1)
                {
                    graph[j].push_back(i);  // Add i as an adjacent bomb to j
                }
            }
        }

        // Perform depth-first search on each bomb to find the maximum number of detonated bombs
        vector<int> visited;  // Keep track of visited bombs
        vector<int> x(n, 0);  // Initialize the visited array with zeros
        int ans = 1;  // Initialize the maximum count of detonated bombs to 1

        // Iterate through each bomb as a starting point for the depth-first search
        for (int i = 0; i < n; i++)
        {
            visited = x;  // Reset the visited array for each iteration
            int temp = dfs(i, visited, graph);  // Perform depth-first search and get the count
            ans = max(ans, temp);  // Update the maximum count if necessary
        }

        return ans;  // Return the maximum count of detonated bombs
    }
};
```

