                if(isInside(b1[0], b1[1], b1[2], b2[0], b2[1])){
                    map.putIfAbsent(i, new ArrayList<>());
                    map.get(i).add(j);
                }
            }
        }

        int max = 0;
        for(int i=0; i<bombs.length; i++){
            int k = dfs(i, new boolean[bombs.length]);
            if(k == bombs.length){ //improves runtime.
                return k;
            }
            max = Math.max(max, k);
        }

        return max;
    }

    private int dfs(int node, boolean[] vs){
        if(vs[node]){
            return 0;
        }
        vs[node] = true;

        int res = 1;
        for(int nei : map.getOrDefault(node, new ArrayList<>())){
            res += dfs(nei, vs);
        }

        return res;
    }

	//if a circle touches or crosses another circle's center then detonating the previos circle will also detonate this circle.
    private boolean isInside(long x1, long y1,
                            long rad, long x, long y){
        long dist = ((x - x1) * (x - x1) + (y - y1) * (y - y1));
        long radius = rad * rad;

        return radius >= dist;
    }
}
