    void dfs(int node, vector<int> &visited, vector<int> adj[], int &cnt){
        visited[node] = 1;
        cnt++;

        for(auto neighbour: adj[node]){
            if(!visited[neighbour]){
                dfs(neighbour, visited, adj, cnt);
            }
        }
