                if(i != j){
                    long long int x, y;
                    x = abs(x0 - bombs[j][0]);
                    y = abs(y0 - bombs[j][1]);

                    if(x*x + y*y <= r0*r0){
                        adj[i].push_back(j);
                    }
                }
            }
