        //call dfs for every node to see how many bombs are connected with that single bomb
        int ans = INT_MIN;
        for(int i=0; i<n; i++){
            int cnt = 0;
            vector<int> visited(n, 0);
            dfs(i, visited, adj, cnt);

            ans = max(ans, cnt);
        }

        //return the bomb with maximum number of connected bombs
        return ans;
    }
};
