class Solution {
public:
    bool check(int xi, int yi, int ri, int xj, int yj){
        // return (long)ri * ri >= (long)(xi - xj) * (xi - xj) + (long)(yi - yj) * (yi - yj); 
        long long c1c2 = (long long )(xi - xj) * (xi - xj) + (long long)(yi - yj) * (yi - yj);
        long long r1r2 = (long long) ri * ri;
        return r1r2 >= c1c2;
    }
    void dfs(int node, vector<vector<int>>&g, vector<int>&vis, int &cnt){
        cnt++;
        vis[node] = 1;
        for(auto i:g[node]){
            if(!vis[i]) dfs(i,g,vis,cnt);
        }
    }
    int maximumDetonation(vector<vector<int>>& bombs) {
        int n = bombs.size();
        vector<vector<int>>g(n);

        for(int i=0; i<n; i++){
            for(int j=0; j<n; j++){
                int xi = bombs[i][0], yi = bombs[i][1], ri = bombs[i][2];
                int xj = bombs[j][0], yj = bombs[j][1];
                if(i!=j && check(xi,yi,ri,xj,yj)){
                    g[i].push_back(j);
                } 
            }
        }
        int ans = 0;
        for(int i=0; i<n; i++){
            int cnt = 0;
            vector<int>vis(n,0);
            dfs(i,g,vis,cnt);
            ans = max(ans,cnt);
        }
        return ans;
    }
};
