                indegree[who[u]] += 1

        for u in who:
            weights[who[u]] += 1

        q = deque()
        for u in indegree:
            if indegree[u] == 0:
                q.append(u)

        res = {u: set([u]) for u in representatives}
        visited = set()
        while q:
            for _ in range(len(q)):
                node = q.popleft()
                for child in new_adj[node]:
                    if child in visited:
                        continue

                    # // Update set to avoid double counts
                    res[child].update(res[node])

                    indegree[child] -= 1
                    if indegree[child] == 0:
                        q.append(child)
                        visited.add(child)

        # // Sum weights inside the set and take max
        maxb = 0
        for u in res:
            curr = 0
            for i in res[u]:
                curr += weights[i]
            maxb = max(maxb, curr)

        return maxb


class Bomb:
    def __init__(self, x, y, r):
        self.x = x
        self.y = y
        self.r = r

    def enclosed(self, other):
        return self.r ** 2 >= (self.x - other.x) ** 2 + (self.y - other.y) ** 2
```

