class Solution {
    func maximumDetonation(_ bombs: [[Int]]) -> Int {

        func contains(_ a:[Int], _ b:[Int]) -> Bool {
            let (deltaX, deltaY) = (a[0]-b[0], a[1]-b[1])
            return deltaX * deltaX + deltaY * deltaY <= a[2] * a[2]
        }

        let bombsInRadius = bombs.indices.map { i in
            bombs.indices.filter { j in contains(bombs[i], bombs[j]) }
        }

        func bfs(_ start: Int) -> Int {
            var bfs = Set([start]), visited = bfs
            while !bfs.isEmpty {
                bfs = Set(bfs.flatMap { bomb in bombsInRadius[bomb]}).subtracting(visited)
                visited.formUnion(bfs)
            }
            return visited.count
        }

        return bombs.indices.map(bfs).max()!
    }
}
```

