class Solution {
    public int maximumDetonation(int[][] bombs) {
        int n = bombs.length;
        List<Integer>[] graph = new List[n];
        for (int i = 0; i < n; i++) graph[i] =  new ArrayList<Integer>();
		// O(n^2 /2)  by using j = i+1
		 for (int i = 0; i < n; i++) {
            for (int j = i+1; j < n; j++) {
                // use double since multiplying large values like 10^5 will cause overflow
                double dx = bombs[i][0] - bombs[j][0];
                double dy = bombs[i][1] - bombs[j][1];
                double r1  = bombs[i][2], r2  = bombs[j][2];
                double dist =  dx * dx + dy * dy;
                if ( dist <= r1 * r1) graph[i].add(j);
                if ( dist <= r2 * r2) graph[j].add(i);
            }

        }
        boolean[] visited = new boolean[n];
        int ans = 0;
        for (int i = 0; i < n; i++) {
            ans = Math.max(ans, dfs(graph, i, visited));
            if (ans == n) return ans; //all of the nodes can be visited
            Arrays.fill(visited, false);
        }
        return ans;
    }
    private int dfs(List<Integer>[] graph, int i, boolean[] visited) {
        int cc = 0;
        if (visited[i]) return 0;
        visited[i] = true;
        for (int neigh: graph[i]) {
            cc += dfs(graph, neigh, visited);
        }
        return cc + 1;
    }

}
