        int res = 0;
        Map<Integer, Set<Integer>> cache = new HashMap<>(); 
        for (int i = 0; i < n; i++) {
            Set<Integer> exploded = new HashSet<>(); 
            dfs(i, bombs, exploded, cache);
            cache.put(i, exploded);
            res = Math.max(res, exploded.size());
        }

        return res;
    }

    private void dfs(int current, int[][] bombs, Set<Integer> visited, Map<Integer, Set<Integer>> cache) {
        if (cache.containsKey(current)) {
            visited.addAll(cache.get(current));
            return;
        }

        visited.add(current);
        int n = bombs.length;
        for (int i = 0; i < n; i++) {
            if (!visited.contains(i) && inRange(bombs[current], bombs[i])) {
                visited.add(i);
                dfs(i, bombs, visited, cache);
            }
        }
    }

    private boolean inRange(int[] a, int[] b) {
        long dx = a[0] - b[0], dy = a[1] - b[1], r = a[2];
        return dx * dx + dy * dy <= r * r;
    }
}
