    int maximumDetonation(vector<vector<int>>& bombs) 
    {

        int n = bombs.size();
         vector<vector<int>>adj(n);
        for(int i=0;i<n;i++)
        {
            int x1 = bombs[i][0];
            int y1 = bombs[i][1];
            int r1 = bombs[i][2];
            for(int j=0;j<n;j++)
            {
                int x2 = bombs[j][0];
                int y2 = bombs[j][1];
                int r2 = bombs[j][2];
                long long int  xx = abs(x2-x1);
                long long int yy = abs(y2-y1);
                long long int rr =r1;  
                if((xx*xx + yy*yy) <= (rr*rr))
                {
                    adj[i].push_back(j);

