class Solution {
public:
    int dsfind(vector<int > &parent, int x)// with path compression
    {
        if(parent[x]==x)
        return x;

        int res = dsfind(parent, parent[x]);
        parent[x] = res;
        return res;
    }
    void dsunion(vector<int > &parent, int x, int y)// with no improvement
    {
        int parentx = dsfind(parent, x);
        int parenty = dsfind(parent, y);

        if(parentx==parenty)
        return;
        parent[parenty] = parentx; // or reverse
    }
    int maximumDetonation(vector<vector<int>>& bombs) {
        int n = bombs.size();
        vector<int > parent(n,0);
        for(int i=0;i<n;i++)
        {
            parent[i]=i;
        }
        for(int i=0;i<n-1;i++)
        {
            for(int j=i+1;j<n;j++)
            {
                long long int x1 = (long long int)(bombs[i][0]-bombs[j][0])*(bombs[i][0]-bombs[j][0]);
                long long int x2 = (long long int)(bombs[i][1]-bombs[j][1])*(bombs[i][1]-bombs[j][1]);

                int dist = sqrt(x1+x2);
                if(dist<=bombs[i][2]+bombs[j][2])
                {
                    dsunion(parent,i,j);
                }
            }
        }
        map<int, int> mp;
        for(int i=0;i<n;i++)
        {
            mp[dsfind(parent,parent[i])]++;
        }
        int ans = INT_MIN;
        for(auto it:mp)
            ans=max(ans, it.second);
        return ans;
    }
};
