class Solution {
public:
	// function to check if a point lies inside or on the circle
    bool isInside(long long cx, long long cy,long long r, long long x, long long y)
    {

        if ((x-cx)*(x-cx)+(y-cy)*(y-cy)<=pow(r,2))
            return true;
        else
            return false;
    }

	// dfs for checking if the point lies inside or on for every elt of array
    void dfs(vector<vector<int>>&b,int k,vector<int>&vis,vector<int>&vis1)
    {
        if(vis1[k]==1)
            return;

        vis1[k]=1;
        for(int i=0;i<b.size();i++)
        {
			// check for neighbouring bombs which gets detonated
            if(i!=k)
            if(isInside(b[k][0],b[k][1],b[k][2],b[i][0],b[i][1]))
            {
                if(vis1[i]!=1)
                dfs(b,i,vis,vis1);
            } 
        }
        vis[k]=1;
    }
    int maximumDetonation(vector<vector<int>>& b) {

        // minimum ans is 1(atleast 1 bomb will detonate)
        int ans=1;


        for(int i=0;i<b.size();i++)
        {
            int s=1;
            vector<int>vis(b.size(),0),vis1(b.size(),0);
            //b[i]=1;
            vis[i]=1;

			//calling dfs
            dfs(b,i,vis,vis1);

            // if the the array vis is marked as 1 then the bomb is detonated
            for(auto it :vis)
                if(it==1)
                    s++;
            if(s>=ans)
            {
                ans=s;
            }
        }
        return ans-1;
    }

};
