```
/*

Implicit Graph
    * node : Bombs represented by their centre coordinates and radius
    * neighbors/edges : If bombOne can detonate bombTwo there is an edge from bombOne to bombTwp (NOT neccesaarily other way around)

    Return the size of the largest connected component.

*/

class Solution {
public:
    int maximumDetonation(vector<vector<int>>& bombs) {
        int numMaxBombsDetonated = 0;

        unordered_map<int, vector<int>> graph; // Adjacency List : {0 : [1, 2, 4], 1 : [0, 3, 6]}
        buildGraph(bombs, graph);

        for(int node = 0 ; node < bombs.size() ; node++) {
            unordered_set<int> visited; // Directed Graph : One node can be part of multiple connected components. So use new visited every time.

            if(visited.find(node) != visited.end())
                continue;

            int numOfCurrBombsDetonated = getSizeOfAndMarkConnectedComponentDFS(graph, node, visited); // Visit a node only once in a connected component.

            numMaxBombsDetonated = max(numMaxBombsDetonated, numOfCurrBombsDetonated);
        }

        return numMaxBombsDetonated;
    }

private:
    float getDistBetween(int x_One, int y_One, int x_Two, int y_Two) {
        float distBetweenPoints = 0.0;

        int x_diff_abs = abs(x_One - x_Two);
        int y_diff_abs = abs(y_One - y_Two);

        float sumOfSquaresDist = pow( (x_diff_abs), 2) + pow( (y_diff_abs), 2);

        distBetweenPoints = pow( (sumOfSquaresDist), 0.5);

        return distBetweenPoints;
    }

    bool doesEdgeExist(vector<int>& bombOne, vector<int>& bombTwo) {
        int x_Center_bombOne = bombOne[0];
        int y_Center_bombOne = bombOne[1];
        int radius_bombOne = bombOne[2];

        int x_Center_bombTwo = bombTwo[0];
        int y_Center_bombTwo = bombTwo[1];
        int radius_bombTwo = bombTwo[2];

        float dist_bombOne_bombTwo = getDistBetween(x_Center_bombOne, y_Center_bombOne, x_Center_bombTwo, y_Center_bombTwo);

        if(dist_bombOne_bombTwo <= radius_bombOne)
            return true;

        return false;
    }

    void buildGraph(vector<vector<int>>& bombs, unordered_map<int, vector<int>>& graph) {
        // Initialize
        for(int node = 0 ; node < bombs.size() ; node++) {
            graph[node] = vector<int>();
        }

        // Add the edges
        for(int index = 0 ; index < bombs.size() - 1 ; index++) {
            for(int subIndex = index + 1 ; subIndex < bombs.size() ; subIndex++) {

                vector<int> bombOne = bombs[index];
                vector<int> bombTwo = bombs[subIndex];

                if(doesEdgeExist(bombOne, bombTwo))
                    graph[index].push_back(subIndex);

                if(doesEdgeExist(bombTwo, bombOne)) // Don't use else-if. We want to check for edges both ways not either ways.
                    graph[subIndex].push_back(index);

            }
        }

        return;
    }

    int getSizeOfAndMarkConnectedComponentDFS(unordered_map<int, vector<int>>& graph, int currNode, unordered_set<int>& visited) {
        // Base Case(s)
        if(visited.find(currNode) != visited.end())
            return 0;

        // Process Node
        int sizeOfConnectedComponentFromCurr = 1;
        visited.insert(currNode);

        // Recurse on Neighbors
        vector<int> neighborNodes = graph[currNode];

        for(auto neighborNode : neighborNodes) {
            int sizeOfConnectedComponentFromNeighbor = getSizeOfAndMarkConnectedComponentDFS(graph, neighborNode, visited);

            sizeOfConnectedComponentFromCurr += sizeOfConnectedComponentFromNeighbor;
        }

        return sizeOfConnectedComponentFromCurr;
    }
};
