class Solution {
public:
    int fodega(vector<int> &v1,vector<int> &v2){
        long long a=v1[0]-v2[0];
        long long b=v1[1]-v2[1];
        if((a*a)+(b*b) <= pow((v1[2]),2)) {
  return 1;
        }
        return 0;
    }

    int bfs(int point, vector<vector<int>> &adj,unordered_map<int,bool> vis) {
        int count=0;
        queue<int> q;
      q.push(point);
      vis[point]=1;

      while(!q.empty()){
          int t=q.front();
           q.pop(); count++;

          for(auto &i:adj[t]){
              if(!vis[i]){
                q.push(i);
                vis[i]=1;
              } 
          }
      }
        return count;
    }

    int maximumDetonation(vector<vector<int>>& bombs) {
        int ans=0;int n=bombs.size();
        unordered_map<int ,bool> vis;
        vector<vector<int>> adj;

        //prepare adjacency list

        for(int i=0;i<bombs.size();i++){
            vector<int>temp;
            for(int j=0;j<n ;j++){
                if(fodega(bombs[i],bombs[j])==1 && j!=i) {
                temp.push_back(j);
                }
            }
        adj.push_back(temp);
        }

        for(int i=0;i<n;i++){
            if(!vis[i]){
                ans=max(ans, bfs(i,adj,vis));
            }
        }
    return ans;
