class Solution {
    int answer= 0;
    int ctr=0;
public:
    bool check(int x,int y,int r){
        long long int value = (long long int)x*x + (long long int)y*y - (long long int)r*r;
        if(value<=0)
            return true;
        return false;
    }

    void dfs(vector<vector<int>>&adjList, vector<bool>&vis,vector<bool>&cyclic,int node){
        vis[node] = true;
        cyclic[node] = true;
        ctr++;
        if(adjList[node].size()==0){
            return;
        }
        for(int i=0; i<adjList[node].size(); i++){
            if(!cyclic[adjList[node][i]])
            {
                dfs(adjList, vis, cyclic, adjList[node][i]);
            }

        }

    }

    int maximumDetonation(vector<vector<int>>& bombs) {
        int n = bombs.size();
        vector<vector<int>>adjList(n);
        for(int i=0; i<n-1; i++){
            for(int j=i+1; j<n; j++){
                if(check(bombs[j][0]-bombs[i][0],bombs[j][1]-bombs[i][1],bombs[i][2])){
                    adjList[i].push_back(j);
                }
                if(check(bombs[j][0]-bombs[i][0],bombs[j][1]-bombs[i][1],bombs[j][2])){
                    adjList[j].push_back(i);
                }
            }
        }

        vector<bool>vis(n,false);
        for(int i=0; i<n; i++){
            vector<bool>cyclic(n,false);
            if(!vis[i]){
                dfs(adjList, vis,cyclic, i);
                 answer = max(answer, ctr);
                ctr=0;
            }
        }
        return answer;
    }
};
