class Solution {
public:
    void dfs(vector<vector<int>>& graph, int start,vector<bool>& vis,int& len){
        if(vis[start]) return;
        vis[start] = true;
        len++;
        for(int node:graph[start]){
            if(!vis[node]) dfs(graph,node,vis,len);
        }

    }
    bool isBombInRange(int x1,int y1,int x2,int y2,int radius){
        float dist = sqrt(pow(x1-x2,2)+pow(y1-y2,2));
        return ceil(dist)<=radius;
    }
    int maximumDetonation(vector<vector<int>>& bombs) {
        vector<vector<int>> graph(bombs.size());
        for(int i=0;i<bombs.size();i++){
            for(int j=i+1;j<bombs.size();j++){
                // if(i!=j){
                    int x1 = bombs[i][0];
                    int y1 = bombs[i][1];
                    int radius1 = bombs[i][2];
                    int x2 = bombs[j][0];
                    int y2 = bombs[j][1];
                    int radius2 = bombs[j][2];
                    if(isBombInRange(x1,y1,x2,y2,radius1)){
                        graph[i].push_back(j);
                    }
                    if(isBombInRange(x1,y1,x2,y2,radius2)) graph[j].push_back(i);
                // }
            }
        }

        // printGraph(graph);


        int ans = 0;
        for(int i=0;i<bombs.size();i++){
            // if(!vis[i]){
                vector<bool> vis(bombs.size(),false);
                int temp = 0;
                dfs(graph,i,vis,temp);
                ans = max(ans,temp);
            // }
        }
        return ans;
    }

    void printGraph(vector<vector<int>>& graph){
        for(int i=0;i<graph.size();i++){
            for(int node:graph[i]) cout<<node<<" ";
            cout<<endl;
        }
    }
};
```

