class Solution {
public:
void addToHashMap(std::unordered_map<int, std::list<int>>& hashMap, int key, int value) {
    // Check if the key is present in the hashmap
    if (hashMap.count(key) > 0) {
        // Key is present, add the value to the existing list
        hashMap[key].push_back(value);
    } else {
        // Key is not present, create a new list and add the value
        std::list<int> newList;
        newList.push_back(value);
        hashMap[key] = newList;
    }
}


int dfs(int cur, std::unordered_set<int>& visited, std::unordered_map<int, std::list<int>>& graph) {
    int count = 1;  // Count the current node as visited
    visited.insert(cur);

    for (int element : graph[cur]) {
      if (visited.count(element) > 0) {
    } else {
        count+=dfs(element, visited, graph);
        }
    }

    return count;
}


