        std::unordered_map<int, std::list<int>> graph;
        int n = bombs.size();

        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(i==j){
                    continue;
                }
                //cout << i << " " << j << endl;
                long long comp = static_cast<long long>(bombs[i][2]) * bombs[i][2];
                long long distance = (static_cast<long long>(bombs[i][0]-bombs[j][0])*(bombs[i][0]-bombs[j][0])) + (static_cast<long long>(bombs[i][1]-bombs[j][1])*(bombs[i][1]-bombs[j][1]));
                //cout << comp << " " << distance << endl;
                if(comp>=distance){
