class Solution {
    vector<bool> vis;
public:
    int maximumDetonation(vector<vector<int>>& bombs) {
        int n = bombs.size();

        vector<int> adj[n];
        for (int i=0; i<n; i++) {
            int x = bombs[i][0];
            int y = bombs[i][1];
            int z = bombs[i][2];

            for (int j=i+1; j<n; j++) {
                int a = bombs[j][0];
                int b = bombs[j][1];
                int c = bombs[j][2];

                int p = abs(x-a);
                int q = abs(y-b);

                if (1LL*p*p + 1LL*q*q <= 1LL*z*z) {
                    adj[i].push_back(j);
                }
                if (1LL*p*p + 1LL*q*q <= 1LL*c*c) {
                    adj[j].push_back(i);
                }
            }
        }

        int ans = 0;

        for (int i=0; i<n; i++) {
            vis = vector<bool> (n);
            ans = max(ans, dfs(adj, i));
        }

        return ans;
    }

    int dfs(vector<int> adj[], int i) {
        vis[i] = 1;

        int ans = 1;
        for (auto j: adj[i]) {
            if (!vis[j])
                ans += dfs(adj, j);
        }

        return ans;
    }
};
```

