        if(dist<=r*r) return true;
        return false;
    }

    int dfs(int ind,vector<vector<int>>& bombs,vector<int>& vis){
        vis[ind]=1;
        int t=0;
        for(int i=0;i<bombs.size();i++){
            if(i==ind)continue;
            if(vis[i]==0 and inRange(bombs[i][0],bombs[i][1],ind,bombs)){
                t+=dfs(i,bombs,vis);
            }    
        }
        return t+1;
    }

    int maximumDetonation(vector<vector<int>>& bombs) {
        int n=bombs.size();

        for(int i=0;i<n;i++){
            vector<int> vis(n,0);
            maxi=max(maxi,dfs(i,bombs,vis) );
        }
        return maxi;
    }
};
```

