bool detonated[100];

static int dfs(int **bombs, int n, float (*dist)[100], int i) {
	int d = 0;
	detonated[i] = true;

	for(int j = 0; j < n; ++j)
		if(!detonated[j] && dist[i][j] <= bombs[i][2])
				d += dfs(bombs, n, dist, j);

	return d+1;
}

int maximumDetonation(int **bombs, int n, int *bombsColSize) {
	float dist[100][100];

	for(int i = 0; i < n; ++i) {
		for(int j = i+1; j < n; ++j) {
			float d = hypotf(bombs[i][0] - bombs[j][0], bombs[i][1] - bombs[j][1]);
			dist[i][j] = d;
			dist[j][i] = d;
		}
	}

	int best = 0;

	for(int i = 0; i < n; ++i) {
		memset(detonated, 0, sizeof detonated);
		int d = dfs(bombs, n, dist, i);
		if(best < d) best = d;
	}

	return best;
}
