class Solution {
    fun maximumDetonation(bombs: Array<IntArray>): Int {
        val n = bombs.size
        var visit = BooleanArray(n){ false }
        var memo = IntArray(n){ -1 }

        fun dfs(id: Int): Int{
            if (visit[id]) return 0
            visit[id] = true

            var count = 1
            val curr = bombs[id]

            for (i in 0 until n) {
