class Solution {
    using ll = long long;
    bool ispossible(vector<int>&a,vector<int>&b){
        ll distance = 1LL*(a[0] - b[0])*(a[0] - b[0]) + 1LL*(a[1]-b[1])*(a[1]-b[1]);
        if(distance<=1LL*a[2]*a[2])return true;//a can destroy b 
        return false;
