    void dfs(int node,vector<int>&vis,vector<int>adj[],int &currentsize){
        vis[node] = 1;
        currentsize++;
        for(auto adjnode : adj[node]){
            if(!vis[adjnode]){
                dfs(adjnode,vis,adj,currentsize);
            }
        }
    }
