        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                if(ispossible(bombs[i],bombs[j])){
                    adj[i].push_back(j);
                }
                if(ispossible(bombs[j],bombs[i])){
                    adj[j].push_back(i);
