class Solution {
    var bombs = [[Int]]()
    func maximumDetonation(_ bombs: [[Int]]) -> Int {
        self.bombs = bombs
        var maxCount = 1
        for i in 0..<bombs.count {
            var count = bfs(i)
            maxCount = max(maxCount, count)
        }
        return maxCount
    }

    func bfs(_ index: Int) -> Int {
        var count = 1
        var queue = [Int]()
        queue.append(index)
        var visited = Array(repeating: false, count: bombs.count)
        visited[index] = true

        while !queue.isEmpty {
            let top = queue.removeFirst()            
            for i in 0..<bombs.count {
                if !visited[i] && reachable(top, i) {
                   queue.append(i)
                   visited[i] = true
                   count += 1 
                }
            }
        }

        return count
    }

    func reachable(_ source: Int, _ target: Int) -> Bool {
        let bombSource = bombs[source]
        let bombTarget = bombs[target]
        let bombDiffX = bombSource[0] - bombTarget[0]
        let bombDiffY = bombSource[1] - bombTarget[1]
        let bombR = bombSource[2]

        return bombR * bombR >= bombDiffX * bombDiffX + bombDiffY * bombDiffY
    }
}
