class Solution {
public:
    int DFS(unordered_map<int, vector<int>> &graph, int bomb, vector<bool> &isVisited) {
        if(isVisited[bomb])
            return 0;
        isVisited[bomb] = true;
        int count = 1;
        for(auto &x: graph[bomb]) {
            count += DFS(graph, x, isVisited);
        }
        return count;
    }

    int maximumDetonation(vector<vector<int>>& bombs) {
        unordered_map<int, vector<int>> graph;
        for(int i = 0; i < bombs.size(); i++) {
            double distance = 0.0;
            for(int j = 0; j < bombs.size(); j++) {
                distance = sqrt(pow((bombs[j][0]-bombs[i][0]), 2) + pow((bombs[j][1]-bombs[i][1]), 2));
                if(distance <= bombs[i][2]) {
                    graph[i].push_back(j);
                }
            }
        }
        int maxDetonated = 0;
        vector<bool> isVisited(bombs.size(), false);
        for(int i = 0; i < bombs.size(); i++) {
            fill(isVisited.begin(), isVisited.end(), false);
            int detonated = DFS(graph, i, isVisited);
            maxDetonated = max(maxDetonated, detonated);
        }
        return maxDetonated;
    }
};
