class Solution {
public:

    #define ll long long

    bool possible(int n, vector<int> &bat, ll int hrs){
        ll int time=0;
        for(int i=0;i<bat.size();i++){
            time += bat[i];
            if(time>=hrs){
                time-=hrs; // passing on extra time provided by battery to run other computer
                n--;
            }
            if(n==0) return true;
        }
        return false;
