        // max. time for which we can run current set of batteries will depend on battery with least operational time 
        sort(bat.begin(),bat.end());
        while(l<r){
            ll int m=l+(r-l+1)/2;

            if(possible(n,bat,m)){
                l=m;
            }else{
                r=m-1;
            }
        }
        return l;
    }
};
