class Solution:
    def maxRunTime(self, n: int, batteries: List[int]) -> int:
        l = len(batteries)
        if n > l:
            return 0
        if n == l:
            return min(batteries)
        batteries.sort()
        curbatteries = []
        for _ in range (n):
            curbatteries.append(batteries.pop())
        left = sum(batteries)
        curbatteries.reverse()
        for i in range(n-1):
            need = (curbatteries[i+1]-curbatteries[i])*(i+1)
            if left < need:
                return curbatteries[i]+left//(i+1)
            left-=need
        return curbatteries[-1] + left//n



