class Solution {
public:
    long long maxRunTime(int n, vector<int>& b) {
      if(n>b.size()) return -1;

      long long sum =0;
      priority_queue<int> maxh;
      for(auto i : b){
         sum+=i;
         maxh.push(i);
      }
      while(maxh.top()>sum/n){
          sum-=maxh.top();
          maxh.pop();
          n--;
      }
      return sum/n;


    }
};
