class Solution {
public:
    bool check(int n, vector<int>& batteries, long long target) {
        long long tot = 0;
        for (long long x: batteries) tot += min(x, target);
        return tot >= n * target;
    }

    long long maxRunTime(int n, vector<int>& batteries) {
        long long sum = 0;
        for (auto x: batteries) sum += x;
        long long lt = 1, rt = sum / n;
        while (lt <= rt) {
            long long mid = (lt + rt) / 2;
            if (check(n, batteries, mid)) lt = mid + 1;
            else rt = mid - 1;
        }
        return lt - 1;
    }
};
