class Solution {
    public long maxRunTime(int n, int[] batteries) {
        // return 0 if there are less batteries than the computers
        if(batteries.length<n){
            return 0;
        }

        int l=batteries.length;

        // Sort batteries in ascending order
        Arrays.sort(batteries);

    //batteries from batteries[start_pos] to batteries[l] are taken initially for n computers
        int pos=l-1;
        int start_pos=l;
        while((l-start_pos)<n){
            start_pos=pos--;
        }

     // all battery power of the remaining batteries are stored in rembat. Now our task to evenly distribute this rembat among the computers who have already n batteries (selected in above step)
        long rembat=0;
        while(pos>=0){
            rembat+=((long)batteries[pos--]);
        }

        long count=0;
        long sum=0;
        long prev=-1;
        long max_minutes=-1;

        pos=start_pos;
        while(pos<l){
            long curr = (long)(batteries[pos++]);
            if(count>0){
                long temp=sum+(curr-prev)*count;
                if(temp>rembat){
                    break;
                }else{
                    sum=temp;
                    max_minutes=curr;
                }
            }

            prev=curr;
            count++;
        }

        if(max_minutes==-1){ 
            long first=(long)batteries[start_pos]+(rembat-sum);
            if(start_pos==(l-1)){
                max_minutes=first;
            }else{
                if(first<=batteries[start_pos+1]){
                    max_minutes=first;
                }else{
                    max_minutes=(long)batteries[start_pos+1];
                }
            }

        }else{
            rembat-=sum;
            max_minutes=prev+(rembat/count);
        }

        return max_minutes;

    }
}
