class Solution {
    public long maxRunTime(int n, int[] batteries) {
        int len = batteries.length;
        int startIndex = len - n;

        Arrays.sort(batteries);
        long[] nBatteries = new long[n];
        for (int i = 0; i < n; i++) {
            nBatteries[i] = batteries[startIndex + i];
        }
        long refills = 0;
        for (int i = 0; i < startIndex; i++) {
            refills += batteries[i];
        }
        int index = 0;
        long maxRunTime = nBatteries[0];
        while (refills > 0 && index + 1 < n) {
            long delta = nBatteries[index + 1] - nBatteries[index];
            long totalDelta = delta * (index + 1);
            if (refills < totalDelta) {
                maxRunTime += (refills / (index + 1));
                return maxRunTime;
            }
            maxRunTime += delta;
            refills -= totalDelta;
            index++;
        }
        maxRunTime += (refills / n);
        return maxRunTime;
    }
}
```

