class Solution {
public:
    typedef long long ll;

    long long maxRunTime(int n, vector<int>& bat) {
        // Convert the given array to array of long long (so, that addition of battery powers may not exceed integer limit.)
        vector<ll> b;
        for(auto x:bat)b.push_back(x);

        // Sort the given battery powers in the ascending order.
        sort(b.begin(),b.end());

        // Extra batteries with minimum possible powers.
        ll m = b.size() - n;

        // Sum of extra battery's powers.
        ll ss = accumulate(b.begin(),b.begin()+m, 0ll);

        // Variables for binary search.
        ll i = b[m], j = b[b.size()-1]+ss;
        ll ans = 0;

        while(i<=j){
            ll mid = i + (j-i)/2;
            // Variable to hold the minimum battery power if we try to distribute 'ss' power over remaining batteries and try to make each battery's power greater than equal to 'mid'.
            ll a = LLONG_MAX;
            ll s = ss;
            for(int i=m;i<b.size();i++){
                ll hh = b[i];
                if(b[i]<mid){
                    if((mid-b[i])<=s){
                        s -= (mid-b[i]);
                        b[i] = mid;
                    }else{
                        b[i]+=s;
                        s = 0;
                    }
                }
                a = min(a, b[i]);
                b[i] = hh;
            }

            if(mid == a){
                ans = mid;
                i = mid+1;
            }else{
                j = mid-1;
            }
        }
        return ans;
    }
};
