using ll = long long;
class Solution {
public:

  // this function checks if the total usable time (totalUsableTime) is greater than or equal 
  // to the required time to run all 'n' computers simultaneously (target * n).
  // If totalUsableTime is greater than or equal to the required time,
  // target time, and the function returns true. Otherwise, it returns false.

  // We add the usable time of batteries to the totalUsableTime variable, which accumulates
  // the total usable time for all computers combined. This step accounts for how
  // much time can be utilized if all batteries were used simultaneously.

  bool canRunAllComputers(int n, vector<int>& batteries, ll target){
    ll totalUsableTime  = 0;
    for(int it:batteries){
      totalUsableTime += it < target ? it : target;
    }
    return totalUsableTime >= target * n;
  }
  ll maxRunTime(int n, vector<int>& batteries) {
    ll totalTime = accumulate(batteries.begin(), batteries.end(), 0LL);
    // cout<<totalTime;
    ll l = 1, r = totalTime / n;
    ll ans = -1;
    while(l <= r){
      ll mid = l + (r - l) / 2;
      if(canRunAllComputers(n, batteries, mid)){
        ans = mid;
        l = mid + 1;
      }
      else r = mid - 1;
    }
    return ans;
  }
};
```

