class Solution:
    def maxRunTime(self, n: int, batteries: List[int]) -> int:

        # array sorting
        batteries.sort()

        # getting the energy of all extra batteries
        extra = sum(batteries[:-n])

        # earlier we sorted an array to get n the largest batteries that our computers will use
        live = batteries[-n:]

        # iterate over an array live
        for i in range(n - 1):

            # if extra has enough energy for our batteries (indexed from 0 to i), we take energy from extra
            # if hasn't, return the value of smallest battery + all energy that can be provided for one battery
            if extra // (i + 1) > live[i + 1] - live[i]: 
                extra -= (i + 1) * (live[i + 1] - live[i])
            else:
                return live[i] + extra // (i + 1) 

        # if we got there, all batteries have equal energy, so we return the last battery with remaining energy, provided for all  batteries
        return live[-1] + extra // n




