        long sum = 0;
        for(int i : batteries){
            sum +=(long) i;
        }

        long startTime = (long) 0, endTime = (long) sum, maxTime = (long) 0;

        while(startTime <= endTime){

            long mid = (long)(startTime+endTime)/2;
            if(RunTime(batteries, mid)>= mid*n){
                maxTime = mid;
                startTime = (long)mid + 1;

            }else{

                endTime = (long)mid-1;

            }


        }

        return maxTime;

    }
}
