class Solution {
public:
    long long maxRunTime(int n, vector<int>& batteries) {
        sort(batteries.begin(),batteries.end(),greater<int>());
        vector<int>v;
        for(int i=0;i<n;i++){
            v.push_back(batteries[i]);
        }
        reverse(v.begin(),v.end());
        long long sum=0;
        for(int i=n;i<batteries.size();i++){
            sum+=batteries[i];
        }
        for(int i=0;i<n-1;i++){
            int diff = v[i+1]-v[i];
            int req = diff*(i+1);
            sum-=req;
            if(sum<0){
                sum+=req;
                long long delta = sum/(i+1);
                return delta+v[i];
            }
        }
        if(sum==0)return v.back();
        return (sum/n)+v.back();
    }
};
```

