class Solution {
public:
    #define ll long long
    long long maxRunTime(int n, vector<int>& batteries) {
        sort(batteries.begin(),batteries.end());
        ll m=batteries.size();
        ll sum=0;
        for(ll i=0;i<m-n;i++)
            sum+=batteries[i];

        ll ans=batteries[m-n];
        ll c=1;
        for(ll i=m-n+1;i<m;i++)
        { 

            ll diff=batteries[i]-batteries[i-1];

            if(c*diff<=sum)
            {
                ans=batteries[i];
                sum-=(c*diff);
            }
            else
            {
                ans+=sum/c;
                sum=0;
                break;
            }
            c++;
        }

        return ans+sum/n;
    }
};
