class Solution {
    // This isValidTime returns true if time required is equal to "time"
    public static boolean isValidTime(int[] bat,int n,long time,long[] sum) {
        for(int i=bat.length-1;i>=0;i--) {
            if(n==0) return true;
            if(bat[i]<time) {
                if(sum[i]/n<time) {
                    return false;
                }
                break;
            }
            n--;
        }
        return true;
    }
    public long maxRunTime(int n, int[] bat) {
        Arrays.sort(bat); // sort in Increasing Order
        int m = bat.length;
        // Our Answer always lie in Between nth largest battery power time to total sum of battery power divided by total computer , as number of computer is always less then batteries ; and this can found easily using Binary Search On Answer
        long lo = bat[m-n];     // nth largest battery power time i.e lower limit
        long[] sum = new long[m];   // stores sum of batteries starting from 0 to m-1 at every index;
        long hi=0;
        for(int i=0;i<m;i++) {
            hi+=(long)bat[i];
            sum[i]=hi;
        }
        hi/=n;  //total sum by n i.e Upper limit
        long ans=lo;    // set answer to low
        while(lo<=hi) {
            long mid = lo+(hi-lo)/2;
            if(isValidTime(bat,n,mid,sum)) {    // if mid Value of time is Valid then we search above mid to hi
                if(ans<mid) ans=mid;    // update our answer
                lo=mid+1;
            } else {
                hi=mid-1;
            }
        }
        return ans;
    }
}
